mapper.select:
SELECT c.comment_id, c.post_id, c.parent_comment_id, c.user_id, u.nickname, c.content, c.status, c.status_changed_at, c.created_by, c.created_at, c.updated_by, c.updated_at FROM comment c JOIN user_info u ON u.user_id = c.user_id WHERE post_id = #{postId}
AND c.user_id NOT IN ( SELECT target_user_id FROM block WHERE user_id = #{userId} )
ORDER BY created_at DESC

mapper.update:
UPDATE comment
SET content = #{content},
    updated_by = #{userId}
WHERE comment_id = #{commentId}

mapper.select:
SELECT COUNT(*)
FROM comment
WHERE parent_comment_id = #{commentId}
