mapper.select:
SELECT p.post_id AS postId, p.board_id AS boardId, p.user_id AS userId, u.nickname AS nickname, p.title AS title, p.content AS content, p.pinned_yn AS pinnedYn, p.status AS status, p.status_changed_at AS statusChangedAt, p.view_count AS viewCount, p.comment_count AS commentCount, p.created_at AS createdAt, p.created_by AS createdBy, p.updated_at AS updatedAt, p.updated_by AS updatedBy, b.board_name AS b_boardName, b.category_id AS b_categoryId, c.category_name AS c_categoryName FROM post p LEFT JOIN user_info u ON p.user_id = u.user_id LEFT JOIN board b ON p.board_id = b.board_id LEFT JOIN category c ON b.category_id = c.category_id
AND p.user_id NOT IN ( SELECT target_user_id FROM block WHERE user_id = #{userId} )
WHERE p.post_id = #{postId}

mapper.update:
UPDATE post
SET
    title = #{title},
    content = #{content},
    updated_by = #{userId}
WHERE post_id = #{postId}

UPDATE post
SET view_count = view_count + 1
WHERE post_id = #{postId}

UPDATE post
SET comment_count = comment_count + 1
WHERE post_id = #{postId}

UPDATE post
SET comment_count = comment_count - 1
WHERE post_id = #{postId}

mapper.select:
SELECT p.post_id AS postId, p.board_id AS boardId, p.user_id AS userId, u.nickname AS nickname, p.title AS title, p.content AS content, p.pinned_yn AS pinnedYn, p.status AS status, p.status_changed_at AS statusChangedAt, p.view_count AS viewCount, p.comment_count AS commentCount, p.created_by AS createdBy, p.created_at AS createdAt, p.updated_by AS updatedBy, p.updated_at AS updatedAt, b.board_id AS b_boardId, b.board_name AS b_boardName, b.category_id AS b_categoryId, c.category_id AS c_categoryId, c.category_name AS c_categoryName FROM post_view pv JOIN post p ON pv.post_id = p.post_id LEFT JOIN user_info u ON p.user_id = u.user_id LEFT JOIN board b ON p.board_id = b.board_id LEFT JOIN category c ON b.category_id = c.category_id WHERE pv.user_id = #{userId}
AND p.user_id NOT IN ( SELECT target_user_id FROM block WHERE user_id = #{userId} )
ORDER BY pv.viewed_at DESC LIMIT 15
