mapper.select:
SELECT COUNT(*)
FROM post
WHERE board_id = #{boardId}

mapper.update:
UPDATE post
SET view_count = view_count + 1
WHERE post_id = #{postId}

UPDATE post
SET comment_count = comment_count + 1
WHERE post_id = #{postId}

UPDATE post
SET comment_count = comment_count - 1
WHERE post_id = #{postId}
