mapper.select:
SELECT post_id, board_id, user_id, login_id, title, content, view_count, comment_count, created_at
FROM post
WHERE board_id = #{boardId}
ORDER BY created_at DESC
LIMIT #{size} OFFSET #{offset}

SELECT post_id, board_id, user_id, login_id, title, content, view_count, comment_count, created_at
FROM post
WHERE post_id = #{postId}

mapper.update:
UPDATE post
SET board_id = #{boardId},
    title = #{title},
    content = #{content},
    updated_by = #{userId}
WHERE post_id = #{postId}
